    //
    (function(window){
      var alien_lid = window.document.getElementById('xeno-alien-lid');
      function blink(pos, min, max, delta) {
        pos += delta;
        if (pos < min) {
          pos = min; delta = -delta;
        }
        else if (pos > max) {
          pos = max; delta = -delta;
        }
        alien_lid.setAttribute('ry', pos);
        window.setTimeout(blink, 100, pos, min, max, delta);
      };
      window.setTimeout(blink, 100, 10, 10, 70, 5);
    })(window);//       




