cc; color: #333; } .cluetip-jtip #cluetip-inner { padding: 0 5px 5px; display: inline-block; } .cluetip-jtip div#cluetip-close { text-align: right; margin: 0 5px 5px; color: #900; } /* jtip arrows */ .clue-right-jtip .cluetip-arrows { background-image: url(images/arrowleft.gif); } .clue-left-jtip .cluetip-arrows { background-image: url(images/arrowright.gif); left: 100%; margin-right: -11px; } .clue-top-jtip .cluetip-arrows { background-image: url(images/arrowdown.gif); top: 100%; left: 50%; margin-left: -11px; height: 11px; width: 22px; } .clue-bottom-jtip .cluetip-arrows { background-image: url(images/arrowup.gif); top: -11px; left: 50%; margin-left: -11px; height: 11px; width: 22px; } /*************************************** =cluetipClass: 'rounded' -------------------------------------- */ .cluetip-rounded { background: transparent url(images/bl.gif) no-repeat 0 100%; margin-top: 10px; margin-left: 12px; } .cluetip-rounded #cluetip-outer { background: transparent url(images/tl.gif) no-repeat 0 0; margin-top: -12px; } .cluetip-rounded #cluetip-title { background-color: transparent; padding: 12px 12px 0; margin: 0 -12px 0 0; position: relative; } .cluetip-rounded #cluetip-extra { position: absolute; display: block; background: transparent url(images/tr.gif) no-repeat 100% 0; top: 0; right: 0; width: 12px; height: 30px; margin: -12px -12px 0 0; } .cluetip-rounded #cluetip-inner { background: url(images/br.gif) no-repeat 100% 100%; padding: 5px 12px 12px; margin: -18px -12px 0 0; position: relative; } .cluetip-rounded div#cluetip-close { text-align: right; margin: 0 5px 5px; color: #009; background: transparent; } .cluetip-rounded div#cluetip-close a { color: #777; } /* rounded arrows */ .clue-right-rounded .cluetip-arrows { background-image: url(images/rarrowleft.gif); } .clue-left-rounded .cluetip-arrows { background-image: url(images/rarrowright.gif); left: 100%; margin-left: 12px; } .clue-top-rounded .cluetip-arrows { background-image: url(images/rarrowdown.gif); top: 100%; left: 50%; margin-left: -11px; height: 11px; width: 22px; } .clue-bottom-rounded .cluetip-arrows { background-image: url(images/rarrowup.gif); top: -23px; left: 50%; margin-left: -11px; height: 11px; width: 22px; } /* stupid IE6 HasLayout hack */ .cluetip-rounded #cluetip-title, .cluetip-rounded #cluetip-inner { zoom: 1; }



            //
			/*
			 * jQuery clueTip plugin
			 * Version 0.9.8  (05/22/2008)
			 * @requires jQuery v1.1.4+
			 * @requires Dimensions plugin (for jQuery versions < 1.2.5)
