$.fn.netropyForm = function(params) {

	var formToXML = function(form) {
		var xml = elementStartTag(form)+"\n";  
		var fields = form.elements;
		for (var i=0;i < fields.length;i++) {
			var f = fields[i];
			// Elements only
			if (f.nodeType==1 && f.name) {
				xml += elementToXML(f)+"\n";
			}
			if (f.nodeType!=1) {
				alert(f);
			}
			}
			xml += elementEndTag(form);
			return xml;
	}

	var elementToXML = function(element) {
		var tag = elementStartTag(element);
		if (element.value) {
			tag += element.value;
		}
		return tag+elementEndTag(element);
	}

	var elementStartTag = function(element) {
		return "<"+element.attributes['name'].value+(element.attributes['id']?" form_id='"+element.attributes['id'].value+"'>":">");
	}

	var elementEndTag = function(element) {
		return "</"+element.attributes['name'].value+">";
	}

	// Params
	var process = params ? params : formToXML; 

  /* Change a form's submission type to ajax */
  this.submit(function(){
    $("body").addClass("waitCursor");
    $.ajax({
      type: this.getAttribute("method"),
      url: this.getAttribute("action"),
      data: process(this),
      processData: false,
      contentType: "text/xml",
      success: function(xml) {
        $('#response').html(xml);
        $("body").removeClass("waitCursor");
      }
    });
    return false;
  });
  return this;
}
