                    //
                        $(function () {
                            install_handlers();
                            runHighScoreScript();
                        });

                        var highscore = 0;

                        var highscore_script_path = 'highscore';

                        function runHighScoreScript() {
                            $.get(highscore_script_path, function (script) {
                                $('#highscore').hide();
                                eval(script);
                                $('#show_cur').hide();
                                $('#show_all').show();
                                $('#high_cap').text('Current Highscore');
                                $('#highscore').fadeIn(1000);
                            });
                            return false;
                        }

                        function updateHighScoreScript(name, seconds) {
                            var script = 'highscore=' + seconds + '; $("#highscore").html("' + seconds + ' (' + name + ')");';
                            $.ajax({
                                type: 'PUT',
                                url: highscore_script_path,
                                data: script,
                                contentType: 'text/javascript',
                                processData: false,
                                success: function () {
                                    document.location.reload();
                                }
                            });
                            // remove success function when sql is implemented (see saveHighScore below)
                        }

                        function loadHighScoreTable() {
                            $.ajax({
                                type: 'GET',
                                url: 'load-highscore',
                                contentType: 'text/xml',
                                processData: true,
                                success: function (xml) {
                                    // since Excel only allows us to select the 
                                    // scores in ascending order, we just build
                                    // the table backwards, from the bottom up.
                                    var table = "</tbody></table>";
                                    table += "<p>If you want to brag about your highscore across server reboots (mobile may run out of batteries), you can <a href='download/highscore.xls' title='Download highscore as an Excel spreadsheet'>download</a> the all time highscore, as the database used simply is an Excel&reg; spreadsheet.</p>";
                                    $(xml).find("highscore score").each(function () {
                                        var row = "<tr><td>";
                                        row += $(this).find("name").text();
                                        row += "</td><td class='score'>";
                                        row += $(this).find("seconds").text();
                                        row += "</td></tr>";
                                        table = row + table;
                                    });
                                    table = "<table class='highscore'><tbody>" + table;
                                    $('#show_all').hide();
                                    $('#show_cur').show();
                                    $('#high_cap').text('All Highscores');
                                    $('#highscore').hide().html(table).fadeIn(1000);
                                }
                            });
                            return false;
                        }

                        function saveHighScore(name, seconds) {
                            updateHighScoreScript(name, seconds);
                            /*
                             * Uncomment this when the sql service is implemented
                            	var xml='<score><name>'+name+'</name><seconds>'+seconds+'</seconds></score>';
                            	$.ajax({ 
                            		type: 'PUT',
                            		url: 'save-highscore',
                            		data: xml,
                            		contentType: 'text/xml',
                            		processData: false,
                            		success: function() { document.location.reload(); }
                            	});
                            */
                        }

                        isNS4 = (document.layers) ? true : false;
                        isIE4 = (document.all && !document.getElementById) ? true : false;
                        isIE5 = (document.all && document.getElementById) ? true : false;
                        isNS6 = (!document.all && document.getElementById) ? true : false;

                        var curX, curY, curX2, curY2, boxX, boxY, moving = 0,
                            touch = 0;
                        var gametime = 0,
                            started = 0,
                            speed;
                        var starttime, endtime, finaltime = 0; //pass finaltime to popup window to ask for initials
                        var enemyxdir = new Array(1, 1, 1, 1);
                        var enemyydir = new Array(1, 1, 1, 1);

                        if (isNS4 || isNS6) {
                            document.captureEvents(Event.MOUSEUP | Event.MOUSEDOWN | Event.MOUSEMOVE);
                        }

                        function install_handlers() {
                            $('#box').mousedown(start).mousemove(checkLocation).mouseup(stop);
                        }

                        function startclock() {
                            var today = new Date();
                            starttime = today.getTime();
                        }

                        function endclock() {
                            var today = new Date();
                            endtime = today.getTime();
                        }

                        function calctime() {
                            var time = (endtime - starttime - 0) / 1000;
                            return time;
                        }

                        function giveposX(divname) {
                            if (isNS4) var posLeft = document.layers[divname].left;
                            else if (isIE4 || isIE5) var posLeft = document.all(divname).style.pixelLeft;
                            else if (isNS6) var posLeft = parseInt(document.getElementById(divname).style.left + "");
                            return posLeft;
                        }

                        function giveposY(divname) {
                            if (isNS4) var posTop = document.layers[divname].top;
                            else if (isIE4 || isIE5) var posTop = document.all(divname).style.pixelTop;
                            else if (isNS6) var posTop = parseInt(document.getElementById(divname).style.top + "");
                            return posTop;
                        }

                        function setposX(divname, xpos) {
                            if (isNS4) document.layers[divname].left = xpos;
                            else if (isIE4 || isIE5) document.all(divname).style.pixelLeft = xpos;
                            else if (isNS6) document.getElementById(divname).style.left = xpos;
                        }

                        function setposY(divname, ypos) {
                            if (isNS4) document.layers[divname].top = ypos;
                            else if (isIE4 || isIE5) document.all(divname).style.pixelTop = ypos;
                            else if (isNS6) document.getElementById(divname).style.top = ypos;
                        }

                        function givesize(divname, dimension) {
                            var divsize = 0;
                            if (dimension == 'y') {
                                if (isNS4) divsize = document.layers[divname].clip.height;
                                else if (isIE4 || isIE5) divsize = document.all(divname).style.pixelHeight;
                                else if (isNS6) divsize = parseInt(document.getElementById(divname).style.height + "");
                            } else if (dimension == 'x') {
                                if (isNS4) divsize = document.layers[divname].clip.width;
                                else if (isIE4 || isIE5) divsize = document.all(divname).style.pixelWidth;
                                else if (isNS6) divsize = parseInt(document.getElementById(divname).style.width + "");
                            }
                            return divsize;
                        }

                        // check to see if 'box' is touching 'enemy1'
                        function checktouching(num) {

                            var enemy = "enemy" + num;
                            var difX = giveposX('box') - giveposX(enemy) - 0; // -0 converts to integer
                            var difY = giveposY('box') - giveposY(enemy) - 0;

                            // set touch = 1 if it is touching an enemy
                            if (difX > (-1 * givesize('box', 'x')) && difX < givesize(enemy, 'x') && difY > (-1 * givesize('box', 'y')) && difY < givesize(enemy, 'y')) {
                                touch = 1;
                            } else {
                                touch = 0;
                            }
                        }

                        function movenemy(num, step_x, step_y) {

                            var enemy = "enemy" + num;
                            var enemyx = givesize(enemy, 'x');
                            var enemyy = givesize(enemy, 'y');

                            if (giveposX(enemy) >= (450 - enemyx) || giveposX(enemy) <= 0) {
                                enemyxdir[num] = -1 * enemyxdir[num];
                            }
                            if (giveposY(enemy) >= (450 - enemyy) || giveposY(enemy) <= 0) {
                                enemyydir[num] = -1 * enemyydir[num];
                            }

                            var newposx = giveposX(enemy) + (step_x * enemyxdir[num]) + 0;
                            var newposy = giveposY(enemy) + (step_y * enemyydir[num]) + 0;

                            setposX(enemy, newposx);
                            setposY(enemy, newposy);

                            checktouching(num + "");
                            if (touch == 1) {
                                stop();
                                reset();
                            }
                        }

                        function movenemies() {

                            gametime = gametime + 1

                            if (gametime >= 0 && gametime < 100) speed = 80;
                            else if (gametime >= 100 && gametime < 200) speed = 60;
                            else if (gametime >= 200 && gametime < 300) speed = 40;
                            else if (gametime >= 300 && gametime < 400) speed = 30;
                            else if (gametime >= 400 && gametime < 500) speed = 20;
                            else speed = 10;
                            // window.status = "speed:  " + speed + "   gametime: " + gametime;

                            movenemy(0, -10, 12);
                            movenemy(1, -12, -20);
                            movenemy(2, 15, -13);
                            movenemy(3, 17, 11);

                            setTimeout(movenemies, speed);
                        }

                        function start(e) {

                            if (started == 0) {
                                movenemies();
                                startclock();
                                started = 1;
                            }

                            curX = (isNS4 || isNS6) ? e.pageX : window.event.x;
                            curY = (isNS4 || isNS6) ? e.pageY : window.event.y;

                            curX2 = eval(curX - 40);
                            curY2 = eval(curY - 40);

                            boxX = eval(curX - 20);
                            boxY = eval(curY - 20);

                            var boxleft = giveposX('box');
                            var boxtop = giveposY('box');

                            if (curX > boxleft && curX2 < boxleft && curY > boxtop && curY2 < boxtop) {

                                moving = 1;
                                setposX('box', boxX);
                                setposY('box', boxY);

                                if (isNS4 || isNS6) {
                                    document.captureEvents(Event.MOUSEMOVE);
                                }
                            }
                        }

                        function stop(e) {
                            moving = 0;
                            if (isNS4 || isNS6) {
                                document.releaseEvents(Event.MOUSEMOVE);
                            }
                        }

                        function reset(e) {
                            endclock();
                            moving = 0;
                            if (isNS4 || isNS6) {
                                document.releaseEvents(Event.MOUSEMOVE);
                            }
                            if (finaltime == 0) {
                                finaltime = calctime();
                                if (finaltime > highscore) {
                                    var name = window.prompt('You survived ' + finaltime + ' seconds!\nNew highscore!\nPlease enter your name') || 'Anonymous Coward';
                                    saveHighScore(name, finaltime);
                                } else {
                                    window.alert('You survived ' + finaltime + ' seconds !');
                                    document.location.reload();
                                }
                            }
                        }

                        function checkLocation(e) {

                            curX = (isNS4 || isNS6) ? e.pageX : window.event.x;
                            curY = (isNS4 || isNS6) ? e.pageY : window.event.y;

                            boxX = eval(curX - 20);
                            boxY = eval(curY - 20);

                            checktouching('1');

                            if (moving == 1 && touch == 0) {
                                setposX('box', boxX);
                                setposY('box', boxY);

                                if (curY > 69 && curX > 69 && curY < 381 && curX < 381) return false;

                                stop();
                                reset();
                            } else if (touch == 1) {
                                stop();
                                reset();
                            }

                        }
