                    //
                        function loadXML(service, target) {
                            $("body").addClass("waitCursor");
                            $('#result')[0].value = 'Loading...';
                            $.ajax({
                                type: "GET",
                                url: service,
                                processData: false,
                                complete: function (request) {
                                    $("body").removeClass("waitCursor");
                                    if (request.status / 100 == 2) {
                                        $('#config')[0].value = request.responseText;
                                        $('#mimetype')[0].value = request.getResponseHeader("Content-Type");
                                    } else {
                                        alert("Could not get data: " + request.status + " " + request.statusText);
                                    }
                                    $('#result')[0].value = '';
                                }
                            });
                        }

                        function saveXML(service, fragment, mimetype, target) {
                            $("body").addClass("waitCursor");
                            $('#result')[0].value = 'Saving...';
                            $.ajax({
                                type: "PUT",
                                url: service,
                                data: fragment,
                                processData: false,
                                contentType: mimetype,
                                complete: function (request) {
                                    $("body").removeClass("waitCursor");
                                    $('#result')[0].value = '' + request.responseText;
                                    if (request.status == 200 || request.status == 201) {
                                        $('#result')[0].value = '' + request.responseText;
                                    } else {
                                        $('#result')[0].value = 'Error reply:\n' + request.responseText;
                                        alert("Could not put data: " + request.status + " " + request.statusText);
                                    }
                                }
                            });
                        }

                        function loadBluePrint(service) {
                            $.ajax({
                                type: "GET",
                                url: '/blueprint/plain' + service,
                                processData: false,
                                complete: function (request) {
                                    $("body").removeClass("waitCursor");
                                    if (request.status / 100 == 2) {
                                        $('#blueprint').html('<pre class="prettyprint"/>').text(request.responseText);
                                        prettyPrint();
                                    } else {
                                        alert("Could not get data for" + service + ": " + request.status + " " + request.statusText);
                                    }
                                }
                            });
                        }
