$(document).ready(function () {
    $("#writer").submit(function () {
        var xml = '<p>' + this.content.value + '</p>';
        var options = {
            url: this.getAttribute("action"),
            type: this.getAttribute("method"),
            processData: false,
            data: xml,
            contentType: "text/xml",
            complete: function (xhr) {
                if (xhr.status == 201) {
                    $('#display').get(0).contentDocument.location.reload(true);
                }
            }
        };
        $.ajax(options);
        return false;
    });
});//




